class Foo {
  int x = 0 ;
  int y = 10 ;

  int getZ() {
    return y * 2 ;
  }

  int calcB(int b1 , int b2) {
    return y * b1 * b2 ;
  }

  void test(int a) {
    var z = getZ();
    var b = calcB(z , 3);
    var s = '$this > a: $a ; x: $x ; y: $y ; z: $z ; b: $b' ;
    print(s);
  }
}
