class Foo {
  int x = 0 ;
  int y = 10 ;

  int getZ() {
    return y * 2 ;
  }

  int calcB(int b1 , int b2) {
    return y * b1 * b2 ;
  }


  int IF(bool b, int p1, int p2){
    if(b){
      return p1;
    }else{
      return p2;
    }
  }

  public int test(int a) {
    var z = getZ();
    var b = calcB(z , 3);
    var s = this +" > a: "+ a +" ; x: "+ x +" ; y: "+ y +" ; z: "+ z +" ; b: "+ b ;
    print(s);
    // Comment X
    var f = IF(1 > 2, 1, 2);
    print(f) ;
    // Comment X
    return b;
  }
}
